example.code:
// Lambda style
NSNotificationCenter.DefaultCenter.AddObserver (
        UIFocusUpdateContext.DidUpdateNotification, (notification) => {Console.WriteLine ("Received the notification UIFocusUpdateContext", notification); }


// Method style
void Callback (NSNotification notification)
{
    Console.WriteLine ("Received a notification UIFocusUpdateContext", notification);
}

void Setup ()
{
    NSNotificationCenter.DefaultCenter.AddObserver (UIFocusUpdateContext.DidUpdateNotification, Callback);
}

